import React, { useState } from "react";
import axios from "axios";
import "."
function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [foodPreference, setFoodPreference] = useState("No");
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchHotels = async () => {
    if (!place || !members || !budget) {
      setError("Please fill all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members,
        budget,
        foodIncluded: foodPreference === "Yes",
      });

      setHotels(response.data.hotels);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="container">
      <h1>üåç Travel Guide AI</h1>
      <div className="input-group">
        <input
          type="text"
          placeholder="Enter Place"
          value={place}
          onChange={(e) => setPlace(e.target.value)}
        />
        <input
          type="number"
          placeholder="Number of Members"
          value={members}
          onChange={(e) => setMembers(e.target.value)}
        />
        <input
          type="number"
          placeholder="Budget (‚Çπ)"
          value={budget}
          onChange={(e) => setBudget(e.target.value)}
        />
        <select
          value={foodPreference}
          onChange={(e) => setFoodPreference(e.target.value)}
        >
          <option value="No">No Food</option>
          <option value="Yes">Food Required</option>
        </select>
        <button onClick={fetchHotels}>
          {loading ? "Searching..." : "Find Hotels"}
        </button>
      </div>

      {error && <p className="text-red-500 text-center mt-4">{error}</p>}

      {hotels.length > 0 && (
        <div className="hotels">
          <h3>üè® Recommended Hotels:</h3>
          {hotels.map((hotel, index) => (
            <div key={index} className="hotel-card">
              <h4>{hotel.name}</h4>
              <p>üìç {hotel.location}</p>
              <p>üí∞ Price: ‚Çπ{hotel.price?.startingFrom || hotel.price || "N/A"}</p>
              <p>
                üçΩ Food Included:{" "}
                <span style={{ color: hotel.foodIncluded ? "green" : "red" }}>
                  {hotel.foodIncluded ? "Yes" : "No"}
                </span>
              </p>
              <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer">
                üåç View on Google Maps
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
