import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [foodPreference, setFoodPreference] = useState(false);
  const [hotels, setHotels] = useState([]);
  const [weather, setWeather] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchHotels = async () => {
    if (!place || !members || !budget) {
      setError("Please fill all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);
    setWeather(null);
    setImageUrl("");

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members: parseInt(members, 10),
        budget: parseInt(budget, 10),
        foodIncluded: foodPreference,
      });

      setHotels(response.data.hotels);
      setWeather(response.data.weather);
      setImageUrl(response.data.imageUrl);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="form-container">
        <h1>üåç Travel Guide AI</h1>
        <div className="input-group">
          <input
            type="text"
            placeholder="Enter Place"
            value={place}
            onChange={(e) => setPlace(e.target.value)}
          />
          <input
            type="number"
            placeholder="Number of Members"
            value={members}
            onChange={(e) => setMembers(e.target.value)}
          />
          <input
            type="number"
            placeholder="Budget (‚Çπ)"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
          />
          <label>
            <input
              type="checkbox"
              checked={foodPreference}
              onChange={() => setFoodPreference(!foodPreference)}
            />
            Food Included?
          </label>
          <button onClick={fetchHotels}>
            {loading ? "Searching..." : "Find Hotels"}
          </button>
        </div>

        {error && <p className="error-message">{error}</p>}
      </div>

      {imageUrl && (
        <div className="image-container">
          <h3>üåç Tourist Destination</h3>
          <img src={imageUrl} alt="Tourist Place" className="tourist-image" />
        </div>
      )}

      {weather && (
        <div className="weather-container">
          <h3>‚òÄÔ∏è Weather in {place}</h3>
          <p>üå°Ô∏è Temperature: {weather.temp}¬∞C</p>
          <p>üå§Ô∏è Condition: {weather.description}</p>
          <p>üíß Humidity: {weather.humidity}%</p>
        </div>
      )}

      {hotels.length > 0 && (
        <div className="hotels-container">
          <h3>üè® Recommended Hotels:</h3>
          <div className="hotels-grid">
            {hotels.map((hotel, index) => (
              <div key={index} className="hotel-card">
                <h4>{hotel.name}</h4>
                <p>üìç {hotel.location}</p>
                <div className="price-container">
    <p className="with-food">üí∞ With Food: ‚Çπ{hotel.price?.withFood ?? "N/A"}</p>
    <p className="without-food">üí∞ Without Food: ‚Çπ{hotel.price?.withoutFood ?? "N/A"}</p>
  </div>
                <p>üìû Contact: <a href={`tel:${hotel.phone}`}>{hotel.phone}</a></p>
                <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer" style={{maringt}}>üåç View on Google Maps</a>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
