import React, { useState } from "react";
import axios from "axios";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [roomType, setRoomType] = useState("Standard");
  const [foodPreference, setFoodPreference] = useState("No"); // Default to "No"
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchHotels = async () => {
    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members,
        budget,
        roomType,
        foodIncluded: foodPreference === "Yes", // Convert to boolean
      });

      setHotels(response.data.hotels);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="w-full max-w-xl bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-3xl font-bold text-center text-indigo-700 mb-6">🏨 Room Booking Form</h2>

        {/* Form Fields */}
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Destination</label>
            <input
              type="text"
              placeholder="Enter Place"
              className="w-full p-3 border rounded-md focus:ring focus:ring-indigo-300 shadow-sm"
              value={place}
              onChange={(e) => setPlace(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Number of Guests</label>
            <input
              type="number"
              placeholder="Number of Members"
              className="w-full p-3 border rounded-md focus:ring focus:ring-indigo-300 shadow-sm"
              value={members}
              onChange={(e) => setMembers(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Budget (₹)</label>
            <input
              type="number"
              placeholder="Budget (₹)"
              className="w-full p-3 border rounded-md focus:ring focus:ring-indigo-300 shadow-sm"
              value={budget}
              onChange={(e) => setBudget(e.target.value)}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Room Type</label>
            <select
              className="w-full p-3 border rounded-md focus:ring focus:ring-indigo-300 shadow-sm"
              value={roomType}
              onChange={(e) => setRoomType(e.target.value)}
            >
              <option value="Standard">Standard</option>
              <option value="Deluxe">Deluxe</option>
              <option value="Suite">Suite</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Food Preference</label>
            <select
              className="w-full p-3 border rounded-md focus:ring focus:ring-indigo-300 shadow-sm"
              value={foodPreference}
              onChange={(e) => setFoodPreference(e.target.value)}
            >
              <option value="No">No Food</option>
              <option value="Yes">Food Included</option>
            </select>
          </div>

          {/* Search Button */}
          <button
            onClick={fetchHotels}
            className="w-full bg-indigo-600 text-white p-3 rounded-md hover:bg-indigo-700 transition duration-300"
          >
            {loading ? "Searching..." : "Find Hotels"}
          </button>
        </div>

        {/* Error Message */}
        {error && <p className="text-red-500 text-center mt-4">{error}</p>}

        {/* Display Hotel Results */}
        {hotels.length > 0 && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold mb-3 text-gray-800">🏨 Recommended Hotels:</h3>
            <ul className="space-y-4">
              {hotels.map((hotel, index) => (
                <li key={index} className="p-4 border rounded-lg bg-gray-50 shadow-md">
                  <h4 className="font-bold text-lg">{hotel.name}</h4>
                  <p>📍 {hotel.location}</p>
                  <p>💰 Price: ₹{hotel.price?.startingFrom || hotel.price || "N/A"}</p>
                  {hotel.price?.note && <p>📝 {hotel.price.note}</p>}

                  {/* Corrected Food Included Logic */}
                  <p className="text-sm font-medium">
                    🍽 Food Included: 
                    <span className={`ml-2 ${hotel.foodIncluded === true ? "text-green-600" : "text-red-600"}`}>
                      {hotel.foodIncluded === true ? "Yes" : "No"}
                    </span>
                  </p>

                  <a
                    href={hotel.mapLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-500 underline mt-2 inline-block"
                  >
                    🌍 View on Google Maps
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
