import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [type, setType] = useState("Hill Station");
  const [weather, setWeather] = useState(null);
  const [image, setImage] = useState("");
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchData = async () => {
    if (!place || !members || !budget) {
      setError("Please fill all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members,
        budget,
        type,
      });
      setHotels(response.data.hotels);
      setWeather(response.data.weather);
      setImage(response.data.image);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="container">
      <div className="form-container">
        <h1>üåç Travel Guide AI</h1>
        <input type="text" placeholder="Enter Place" value={place} onChange={(e) => setPlace(e.target.value)} />
        <input type="number" placeholder="Number of Members" value={members} onChange={(e) => setMembers(e.target.value)} />
        <input type="number" placeholder="Budget (‚Çπ)" value={budget} onChange={(e) => setBudget(e.target.value)} />
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="Hill Station">Hill Station</option>
          <option value="Beach">Beach</option>
          <option value="City">City</option>
        </select>
        <button onClick={fetchData}>{loading ? "Searching..." : "Find Hotels"}</button>
        {error && <p className="error-message">{error}</p>}
      </div>

      {weather && <p>‚òÄÔ∏è Weather: {weather.temp}¬∞C, {weather.condition}</p>}
      {image && <img src={image} alt={place} className="place-image" />}

      {hotels.length > 0 && (
        <div className="hotels-container">
          <h3>üè® Recommended Hotels:</h3>
          <div className="hotels-grid">
            {hotels.map((hotel, index) => (
              <div key={index} className="hotel-card">
                <h4>{hotel.name}</h4>
                <p>üìç {hotel.location}</p>
                <p>üí∞ Price: ‚Çπ{hotel.price}</p>
                <p>üìû Contact: <a href={`tel:${hotel.phone}`} className="phone-link">{hotel.phone}</a></p>
                <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer">üåç View on Google Maps</a>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
