import React, { useState } from "react";
import axios from "axios";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchHotels = async () => {
    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members,
        budget
      });

      setHotels(response.data.hotels);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="w-full max-w-lg bg-white shadow-md rounded-lg p-6">
        <h2 className="text-2xl font-bold text-center mb-4 text-gray-800">Travel Guide AI</h2>

        <div className="space-y-4">
          <input
            type="text"
            placeholder="Enter Place"
            className="w-full p-2 border rounded-md focus:ring focus:ring-blue-300"
            value={place}
            onChange={(e) => setPlace(e.target.value)}
          />
          <input
            type="number"
            placeholder="Number of Members"
            className="w-full p-2 border rounded-md focus:ring focus:ring-blue-300"
            value={members}
            onChange={(e) => setMembers(e.target.value)}
          />
          <input
            type="number"
            placeholder="Budget (‚Çπ)"
            className="w-full p-2 border rounded-md focus:ring focus:ring-blue-300"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
          />
          <button
            onClick={fetchHotels}
            className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition"
          >
            {loading ? "Searching..." : "Find Hotels"}
          </button>
        </div>

        {error && <p className="text-red-500 text-center mt-3">{error}</p>}

        {hotels.length > 0 && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold mb-2">Recommended Hotels:</h3>
            <ul className="space-y-3">
              {hotels.map((hotel, index) => (
                <li key={index} className="p-3 border rounded-md bg-gray-50 shadow-sm">
                  <h4 className="font-bold">{hotel.name}</h4>
                  <p>üìç {hotel.location}</p>
                  <p>üí∞ Price: ‚Çπ{hotel.price}</p>
                  <p>üçΩ Food Included: {hotel.foodIncluded ? "Yes" : "No"}</p>
                  <a
                    href={hotel.mapLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 underline"
                  >
                    View on Google Maps
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
