import React, { useState } from "react";
import axios from "axios";

function App() {
  const [formData, setFormData] = useState({
    place: "",
    members: "",
    budget: "",
    foodPreference: "No",
    roomType: "Standard",
    checkInDate: "",
    checkOutDate: "",
  });

  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const fetchHotels = async () => {
    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        ...formData,
        foodIncluded: formData.foodPreference === "Yes",
      });

      setHotels(response.data.hotels);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-6">
      <div className="w-full max-w-2xl bg-white shadow-xl rounded-lg p-8">
        <h2 className="text-3xl font-bold text-center text-indigo-700 mb-6">🏨 Room Booking Form</h2>

        {/* FORM INPUTS */}
        <div className="grid grid-cols-2 gap-6">
          {/* Place */}
          <div>
            <label className="block font-medium text-gray-700">Destination</label>
            <input
              type="text"
              name="place"
              placeholder="Enter city or location"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.place}
              onChange={handleChange}
            />
          </div>

          {/* Number of Members */}
          <div>
            <label className="block font-medium text-gray-700">Number of Guests</label>
            <input
              type="number"
              name="members"
              placeholder="Number of people"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.members}
              onChange={handleChange}
            />
          </div>

          {/* Budget */}
          <div>
            <label className="block font-medium text-gray-700">Budget (₹)</label>
            <input
              type="number"
              name="budget"
              placeholder="Enter your budget"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.budget}
              onChange={handleChange}
            />
          </div>

          {/* Food Preference */}
          <div>
            <label className="block font-medium text-gray-700">Food Preference</label>
            <select
              name="foodPreference"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.foodPreference}
              onChange={handleChange}
            >
              <option value="No">No Food</option>
              <option value="Yes">Food Required</option>
            </select>
          </div>

          {/* Room Type */}
          <div>
            <label className="block font-medium text-gray-700">Room Type</label>
            <select
              name="roomType"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.roomType}
              onChange={handleChange}
            >
              <option value="Standard">Standard</option>
              <option value="Deluxe">Deluxe</option>
              <option value="Suite">Suite</option>
            </select>
          </div>

          {/* Check-in Date */}
          <div>
            <label className="block font-medium text-gray-700">Check-in Date</label>
            <input
              type="date"
              name="checkInDate"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.checkInDate}
              onChange={handleChange}
            />
          </div>

          {/* Check-out Date */}
          <div>
            <label className="block font-medium text-gray-700">Check-out Date</label>
            <input
              type="date"
              name="checkOutDate"
              className="w-full p-3 border rounded-lg focus:ring focus:ring-indigo-300 shadow-sm"
              value={formData.checkOutDate}
              onChange={handleChange}
            />
          </div>
        </div>

        {/* SUBMIT BUTTON */}
        <button
          onClick={fetchHotels}
          className="mt-6 w-full bg-indigo-600 text-white p-4 rounded-lg hover:bg-indigo-700 transition duration-300 text-lg font-semibold"
        >
          {loading ? "Searching..." : "Find Hotels"}
        </button>

        {/* ERROR MESSAGE */}
        {error && <p className="text-red-500 text-center mt-4">{error}</p>}

        {/* HOTEL RESULTS */}
        {hotels.length > 0 && (
          <div className="mt-8">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">🏨 Recommended Hotels</h3>
            <ul className="space-y-4">
              {hotels.map((hotel, index) => (
                <li key={index} className="p-4 border rounded-lg bg-gray-50 shadow-md">
                  <h4 className="font-bold text-lg">{hotel.name}</h4>
                  <p>📍 {hotel.location}</p>
                  <p>💰 Price: ₹{hotel.price?.startingFrom || hotel.price || "N/A"}</p>
                  {hotel.price?.note && <p>📝 {hotel.price.note}</p>}
                  <p className="text-sm font-medium">
                    🍽 Food Included:{" "}
                    <span className={`ml-2 ${hotel.foodIncluded ? "text-green-600" : "text-red-600"}`}>
                      {hotel.foodIncluded ? "Yes" : "No"}
                    </span>
                  </p>
                  <a
                    href={hotel.mapLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-500 underline mt-2 inline-block"
                  >
                    🌍 View on Google Maps
                  </a>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
