import React, { useState } from "react";
import axios from "axios";

const TravelGuide = () => {
    const [place, setPlace] = useState("");
    const [members, setMembers] = useState("");
    const [budget, setBudget] = useState("");
    const [foodIncluded, setFoodIncluded] = useState(false);
    const [hotels, setHotels] = useState([]);
    const [weather, setWeather] = useState(null);
    const [imageUrl, setImageUrl] = useState("");

    const fetchTravelGuide = async () => {
        try {
            const response = await axios.post("http://localhost:5000/api/travel-guide", {
                place,
                members,
                budget,
                foodIncluded
            });

            setHotels(response.data.hotels);
            setWeather(response.data.weather);
            setImageUrl(response.data.imageUrl);
        } catch (error) {
            console.error("Error fetching travel guide:", error);
        }
    };

    return (
        <div style={{ maxWidth: "600px", margin: "auto", textAlign: "center" }}>
            <h2>Travel Guide</h2>
            <input type="text" placeholder="Enter place" value={place} onChange={(e) => setPlace(e.target.value)} />
            <input type="number" placeholder="Number of members" value={members} onChange={(e) => setMembers(e.target.value)} />
            <input type="number" placeholder="Budget (‚Çπ)" value={budget} onChange={(e) => setBudget(e.target.value)} />
            <label>
                <input type="checkbox" checked={foodIncluded} onChange={() => setFoodIncluded(!foodIncluded)} />
                Include Food
            </label>
            <button onClick={fetchTravelGuide}>Get Recommendations</button>

            {weather && (
                <div>
                    <h3>Weather in {place}</h3>
                    <p>Temperature: {weather.temp}¬∞C</p>
                    <p>Description: {weather.description}</p>
                    <p>Humidity: {weather.humidity}%</p>
                </div>
            )}

            {imageUrl && (
                <div>
                    <h3>Tourist Spot</h3>
                    <img src={imageUrl} alt="Tourist Spot" style={{ width: "100%", height: "auto", borderRadius: "10px" }} />
                </div>
            )}

            <h3>Hotel Recommendations</h3>
            <ul>
                {hotels.map((hotel, index) => (
                    <li key={index}>
                        <strong>{hotel.name}</strong> - {hotel.location} <br />
                        <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer">Google Maps</a> | üìû {hotel.phone}
                        <p>Price: ‚Çπ{hotel.price.withFood} (with food) / ‚Çπ{hotel.price.withoutFood} (without food)</p>
                        <p>{hotel.foodIncluded ? "üçΩÔ∏è Food included" : "üö´ No food included"}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TravelGuide;
