import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [foodPreference, setFoodPreference] = useState("No");
  const [hotels, setHotels] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchHotels = async () => {
    if (!place || !members || !budget) {
      setError("Please fill all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members,
        budget,
        foodIncluded: foodPreference === "Yes",
      });

      setHotels(response.data.hotels);
    } catch (err) {
      setError("Error fetching data. Try again.");
    }

    setLoading(false);
  };

  return (
    <div className="container">



      {/* Hotel Listing Section */}
      {hotels.length > 0 && (
        <div className="hotels-container">
          <h3>üè® Recommended Hotels:</h3>
          <div className="hotels-grid">
            {hotels.map((hotel, index) => (
              <div key={index} className="hotel-card">
                <h4>{hotel.name}</h4>
                <p>üìç {hotel.location}</p>
                <p>
                  üí∞ Price:{" "}
                  {hotel.price?.withFood !== undefined && hotel.price?.withoutFood !== undefined ? (
                    <>
                      With Food: ‚Çπ{hotel.price.withFood}, Without Food: ‚Çπ{hotel.price.withoutFood}
                    </>
                  ) : (
                    `‚Çπ${hotel.price?.startingFrom || hotel.price || "N/A"}`
                  )}
                </p>
                <p>
                  üçΩ Food Included:{" "}
                  <span style={{ color: hotel.foodIncluded ? "green" : "red" }}>
                    {hotel.foodIncluded ? "Yes" : "No"}
                  </span>
                </p>
                <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer">
                  üåç View on Google Maps
                </a>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
