import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [foodPreference, setFoodPreference] = useState(false);
  const [hotels, setHotels] = useState([]);
  const [weather, setWeather] = useState(null);
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchTravelGuide = async () => {
    if (!place || !members || !budget) {
      setError("‚ö†Ô∏è Please fill in all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);
    setWeather(null);
    setPlaces([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members: Number(members),
        budget: Number(budget),
        foodIncluded: foodPreference,
      });

      setHotels(response.data.hotels);
      setWeather(response.data.weather);
      setPlaces(response.data.topTouristPlaces);
    } catch (err) {
      setError("‚ùå Error fetching data. Try again.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>üåç Travel Guide AI</h1>
      <div className="input-group">
        <input type="text" placeholder="Enter Place" value={place} onChange={(e) => setPlace(e.target.value)} />
        <input type="number" placeholder="Number of Members" value={members} onChange={(e) => setMembers(e.target.value)} />
        <input type="number" placeholder="Budget (‚Çπ)" value={budget} onChange={(e) => setBudget(e.target.value)} />
        <label>
          <input type="checkbox" checked={foodPreference} onChange={() => setFoodPreference(!foodPreference)} />
          Food Included?
        </label>
        <button onClick={fetchTravelGuide} disabled={loading}>{loading ? "üîç Searching..." : "Find Hotels"}</button>
      </div>

      {places.length > 0 && (
        <div className="image-container">
          <h3>üèûÔ∏è Tourist Attractions</h3>
          {places.map((p, index) => (
            <div key={index}>
              <img src={p.image} alt={p.name} />
              <p>{p.name}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
