import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [place, setPlace] = useState("");
  const [members, setMembers] = useState("");
  const [budget, setBudget] = useState("");
  const [foodPreference, setFoodPreference] = useState(false);
  const [hotels, setHotels] = useState([]);
  const [weather, setWeather] = useState(null);
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchTravelGuide = async () => {
    if (!place || !members || !budget) {
      setError("⚠️ Please fill in all fields!");
      return;
    }

    setLoading(true);
    setError("");
    setHotels([]);
    setWeather(null);
    setImages([]);

    try {
      const response = await axios.post("http://localhost:5000/api/travel-guide", {
        place,
        members: parseInt(members, 10),
        budget: parseInt(budget, 10),
        foodIncluded: foodPreference,
      });

      setHotels(response.data.hotels);
      setWeather(response.data.weather);
      setImages(response.data.images || []); // Setting multiple images
    } catch (err) {
      setError("❌ Error fetching data. Try again.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <div className="form-container">
        <h1>🌍 Travel Guide AI</h1>
        <div className="input-group">
          <input
            type="text"
            placeholder="Enter Place"
            value={place}
            onChange={(e) => setPlace(e.target.value)}
          />
          <input
            type="number"
            placeholder="Number of Members"
            value={members}
            onChange={(e) => setMembers(e.target.value)}
          />
          <input
            type="number"
            placeholder="Budget (₹)"
            value={budget}
            onChange={(e) => setBudget(e.target.value)}
          />
          <label>
            <input
              type="checkbox"
              checked={foodPreference}
              onChange={() => setFoodPreference(!foodPreference)}
            />
            Food Included?
          </label>
          <button onClick={fetchTravelGuide} disabled={loading}>
            {loading ? "🔍 Searching..." : "Find Hotels"}
          </button>
        </div>

        {error && <p className="error-message">{error}</p>}
      </div>

      {images.length > 0 && (
        <div className="image-container">
          <h3>🏞️ Tourist Attractions in {place}</h3>
          <div className="image-grid">
            {images.map((img, index) => (
              <div key={index} className="image-card">
                <img src={img.url} alt={img.name} className="tourist-image" />
                <p>{img.name}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {weather && (
        <div className="weather-container">
          <h3>☀️ Weather in {place}</h3>
          <p>🌡️ Temperature: {weather.temp}°C</p>
          <p>🌤️ Condition: {weather.description}</p>
          <p>💧 Humidity: {weather.humidity}%</p>
        </div>
      )}

      {hotels.length > 0 && (
        <div className="hotels-container">
          <h3>🏨 Recommended Hotels:</h3>
          <div className="hotels-grid">
            {hotels.map((hotel, index) => (
              <div key={index} className="hotel-card">
                <h4>{hotel.name}</h4>
                <p>📍 {hotel.location}</p>
                <p>💰 Price:</p>
                <p className="price-item" style={{ marginLeft: "40px" }}>
                  With Food: ₹{hotel.price?.withFood ?? "N/A"}
                </p>
                <p className="price-item" style={{ marginLeft: "40px" }}>
                  Without Food: ₹{hotel.price?.withoutFood ?? "N/A"}
                </p>
                <p>
                  📞 Contact: <a href={`tel:${hotel.phone}`}>{hotel.phone}</a>
                </p>
                <a href={hotel.mapLink} target="_blank" rel="noopener noreferrer">
                  🌍 View on Google Maps
                </a>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
